{
    "name": "Thank You Campaign", // Internal campaign name
    "campaign_type": "DISCOUNT_COUPONS", // This will generate a bulk coupon code campaign 
    "type": "AUTO_UPDATE", // Campaign will automatically add more vouchers as they are requested via publication assignment 
    "activity_duration_after_publishing": "P30D", // Vouchers will only be valid for 30 days after they are published to a customer 
    "join_once": false, // Customers will be able to opt into the campaign more than once 
    "vouchers_count": 0, // No vouchers will be generated with the campaign, they will be auto-updated and generated once triggered by publishing
    "active": true, // The campaign will become active right away, though no vouchers will yet be published
    "voucher": {
        "type": "DISCOUNT_VOUCHER", // This will generate bulk coupon code vouchers within the campaign when using the Create Campaign endpoint
        "discount": {
            "type": "AMOUNT", // Confirms each voucher will give an amount ($) discount 
            "amount_off": 1000, // The amount ($) off you want to provide. Decimals are not used, so this means $10.00. 
            "effect": "APPLY_TO_ORDER" // The discount will be applied to the whole order
        },
        "redemption": {
            "quantity": 1 // Each voucher will have a redemption limit of 1
        },
        "code_config": {
            "length": 5, // Each voucher will have five random characters at the end of the pre-defined prefix
            "charset": "0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ",
            "pattern": "THANKYOU-#####" // THANKYOU will be the predefined prefix of all codes, with 5 random characters at the end
        }
    }
}

{
    "name": "Code Owner Redemption Only", // Internal rule name
    "error": {
        "message": "This code can only be used if you are the owner of the code" // Error message customers will see if they do not meet the criteria of this validation rule in its entirety, or as a fallback display when validation fails
    },
    "rules": {
        "1": { // Indicates the first criteria in the validation rule
            "name": "publication.redeemable_by_linked_customer", // Looking at if the customer attempting to redeem the code has the code published to them (is the owner)
            "conditions": {
                "$is": [
                    true // Requiring that the customer attempting to redeem the code is the owner of the code 
                ]
            },
            "error": {
                "message": "You must be the owner of this code to use it" // Error message customers will see if they do not meet this specific criteria of the validation rule 
            }
        },
        "logic": " 1 " // Confirms criteria 1 of the validation rule must be met for validation.
    },
    "applicable_to": {
        "excluded": [],
        "included": [],
        "included_all": false
    }
}

{
    "related_object_type": "campaign", // Defines the related object. You can choose between voucher, promotion_tier, campaign, earning_rule, distribution, and reward_assignment.
    "related_object_id": "{{campaign-id}}" // Unique related object ID assigned by Voucherify, e.g v_lfZi4rcEGe0sN9gmnj40bzwK2FH6QUno for a voucher.
}

{
  "campaign":{
      "name":"Thank You Campaign 2023" // Bulk code campaign that you want to publish a code from
  },
  "customer": {
    "source_id":"john.doe@voucherify.io" // Source id of the customer you want to publish the code to - any unique identifer.
  },
  "metadata":{}
}

{
    "type": "DISCOUNT_VOUCHER", // This will generate a shared coupon code voucher when using the Create Voucher endpoint
    "code": "FLASHSALE", // Code you want customers to input
    "discount": {
        "type": "PERCENT", // Confirms the voucher will give a percentage discount 
        "percent_off": 15, // The percentage off you want to provide 
        "effect": "APPLY_TO_ORDER" // The discount will be applied to the entire order 
    },
    "redemption": {
        "quantity": null // This voucher will have no redemption limit 
    }
}

{
    "name": "Redeemable on Orders $100 or More", // Internal rule name
    "error": {
        "message": "This code can only be used on orders above $100 or more" // Error message customers will see if they do not meet the criteria of this validation rule in its entirety, or as a fallback display when validation fails 
    },
    "rules": {
        "1": { // Indicates the first criteria in the validation rule
            "name": "order.amount", // Looking at the total order amount 
            "conditions": {
                "$more_than": [ // Requiring the total order amount be more than the number below
                    9999 // Decimals are not used, so this means $99.99
                ]
            },
            "error": {
                "message": "Your order must be $100 or more to use this code"
            } // Error message customers will see if they do not meet this specific criteria of the validation rule 
        },
        "logic": "1 "
    } // Confirms criteria 1 of the validation rule must be met for validation.
}

{
    "related_object_type": "voucher", // Defines the related object. You can choose between voucher, promotion_tier, campaign, earning_rule, distribution, and reward_assignment.
    "related_object_id": "{{voucher-code}}" // Unique related object ID assigned by Voucherify, e.g v_lfZi4rcEGe0sN9gmnj40bzwK2FH6QUno for a voucher.
}

