    "name": "Promotion for Subscribers", // Internal campaign name.
    "campaign_type": "PROMOTION", // This will generate an automatically applied promotion campaign.
    "type": "STATIC", // Because there are no vouchers associated with promotion campaigns, there is no need for the campaign to be able to auto-update, so it will be static.
    "start_date": "2024-02-27T00:00:00.000Z", // First date promotion can be used.
    "expiration_date": null, // Last date promotion can be used.
    "active": true, // The campaign will become active right away once the start date has passed. 
    "promotion": {
        "tiers": [
            {
                "name": "10% OFF Order ($50 Minimum Purchase)", // Internal promotion tier name
                "banner": "10% OFF for spending at least 50$", // External banner that will be shown for the promotion tier to the customer. 
                "action": {
                    "discount": {
                        "type": "PERCENT", // Confirms the promotion will give a percent (%) discount. 
                        "percent_off": 10, // The percentage off you want to provide. 
                        "effect": "APPLY_TO_ORDER" //The discount will be applied to the entire order. 
                    }
                }
            },
            {
                "name": "20% OFF Order ($250 Minimum Purchase)", // Internal promotion tier name - if there is only one promotion tier, as in this case, this will likely be the same as the campaign name. 
                "banner": "20% OFF for spending at least 250$", // External banner that will be shown for the promotion tier to the customer. 
                "action": {
                    "discount": {
                        "type": "PERCENT", // Confirms the promotion will give a percent (%) discount. 
                        "percent_off": 20, // The percentage off you want to provide. 
                        "effect": "APPLY_TO_ORDER" //The discount will be applied to the entire order. 
                    }
                }
            }
        ]
    }
}

{
    "name": "Validation Rule for subscribers paying beetween $50 and $250", // Internal rule name.
    "rules": {
        "1": { // Indicates the first criteria in the validation rule.
            "name": "order.initial_amount", // Looking at the total order amount.
            "conditions": {
                "$more_than": [ // Requiring the total order amount be more than the number below.
                    4999 // Decimals are not used, so this means $499.99.
                ]
            }
        },
        "2": {
            "name": "order.initial_amount",
            "conditions": {
                "$less_than": [ //Requiring the total order amount be less than the number below.
                    25000
                ]
            }
        },
        "3": {
            "name": "customer.metadata", // Looking at the customer metadata.
            "property": "subscribed", // Looking at the Subscription status. It can be either set as 'true' or 'false'.
            "conditions": {
                "$is": [
                    true
                ]
            }
        }
    },
    "logic": "1 AND 2 AND 3", // Confirms criteria 1, 2, and 3 of validation rule must be met for validation.
    "type": "expression",
    "context_type": "campaign.promotion.discount.apply_to_order"
}

